mapper.update:
UPDATE playerdb
SET
    modifiedTime = NOW()
    , event = #{event}
    , name = #{name}
    , location = #{location}
    , backNo = #{backNo}
    , birth = #{birth}
    , preference = #{preference}
    , nation = #{nation}
    , team = #{team}
    , writer = #{writer}
WHERE id = #{id}
